# 将字符串保存为 tmp.patch 文件

content = """diff --git a/astropy/timeseries/core.py b/astropy/timeseries/core.py\nindex 40b00a0..9d8c8d1 100644\n--- a/astropy/timeseries/core.py\n+++ b/astropy/timeseries/core.py\n@@ -77,10 +77,17 @@ def _check_required_columns(self):\n             elif self.colnames[:len(required_columns)] != required_columns:\n \n                 # Show which required columns are missing/misplaced\n                 actual_columns = self.colnames[:len(required_columns)]\n                 missing_columns = [col for col in required_columns if col not in actual_columns]\n                 \n                 if missing_columns:\n                     missing_str = ', '.join(missing_columns)\n                     raise ValueError(\"{} object is invalid - expected columns {} \"\n                                      \"but missing columns {}\"\n                                      .format(self.__class__.__name__, ', '.join(required_columns), missing_str))\n                 else:\n                     raise ValueError(\"{} object is invalid - expected '{}' \"\n                                      \"as the first column{} but found '{}'\"\n                                      .format(self.__class__.__name__, required_columns[0], plural, self.colnames[0]))\n"""

with open("tmp.patch", "w", encoding="utf-8") as file:
    file.write(content)